import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
// import InputAdornment from '@mui/material/InputAdornment';
// import VisibilityIcon from '@mui/material/VisibilityIcon';
import {
  Box,
  TextField,
  Button,
  CloseIcon,
  Typography,
  Paper,
} from '@mui/material';

export default function Signup() {
  const [config, setConfig] = useState({
    name: '',
    email: '',
    password: '',
  });

  let history = useHistory();
  const signup = (e) => {
    e.preventDefault();
    console.log(config);
    history.push('info');
  };
  const signin = (e) => {
    e.preventDefault();
    console.log(config);
    history.push('signin');
  };

  const paperStyle = {
    padding: 20,
    height: '80vh',
    width: 360,
  };

  return (
    <>
    
    <form onSubmit={signup}>
                <Box
                    display="flex"
                    flexDirection={"column"}
                    maxWidth={400}
                    height={600}
                    alignItems="center"
                    justifyContent={"center"}
                    margin="auto"
                    marginTop={10}
                    padding={3}
                    borderRadius={5}
                    boxShadow={"5px 5px 10px #ccc"}
                    sx={{
                        ":hover": {
                            boxShadow: "10px 10px 20px #ccc"
                        },
                    }}>
                    <Avatar sx={{ backgroundColor: "#1bbd7e" }}><LockOutlinedIcon /></Avatar>
                    <Typography variant='h4' color="#057dcd" padding={3} textAlign="center">Sign Up</Typography>
                    <Box sx={{ display: "flex", width: 250, justifyContent: "space-between" }}>
                        Register As
                        <input checked={userData.userType=="User"?true:false} type="radio" name="UserType" value="User" onChange={(e) => handleOnChange(e, "userType")} />
                        User
                        <input type="radio" name="UserType" value="Admin" onChange={(e) => handleOnChange(e, "userType")} />
                        Admin
                    </Box>
                    {userData.userType == "Admin" ? (
                        <TextField
                            id="standard-password-input"
                            label="Secret Key"
                            type="text"
                            variant="standard"
                            autoComplete='off'
                            fullWidth required
                            margin="normal"
                            value={userData.secretKey}
                            onChange={(e) => handleOnChange(e, "secretKey")}
                        />) : null}

                    <TextField
                        id="standard-password-input"
                        label="First Name"
                        type="text"
                        variant="standard"
                        autoComplete='off'
                        fullWidth required
                        margin="normal"
                        value={userData.fName}
                        onChange={(e) => handleOnChange(e, "fName")}
                    />
                    <TextField
                        id="standard-password-input"
                        label="Last Name"
                        type="text"
                        variant="standard"
                        autoComplete='off'
                        fullWidth required
                        margin="normal"
                        value={userData.lName}
                        onChange={(e) => handleOnChange(e, "lName")}
                    />
                    <TextField
                        id="standard-password-input"
                        label="Email"
                        type="email"
                        variant="standard"
                        autoComplete='off'
                        fullWidth required
                        margin="normal"
                        value={userData.email}
                        onChange={(e) => handleOnChange(e, "email")}
                    />
                    <TextField
                        id="standard-password-input"
                        label="Password"
                        type="password"
                        variant="standard"
                        fullWidth required
                        margin="normal"
                        value={userData.password}
                        onChange={(e) => handleOnChange(e, "password")}
                    />
                    <Button sx={{ marginTop: 3, borderRadius: 3 }} type="submit" variant="contained" fullWidth>Sign up</Button>
                    <Link sx={{ marginTop: 3 }} href="/" variant="body2" >
                        Already have an account? Sign in
                    </Link>
                </Box>
            </form>
    </>
  );
}
