import React, { useState } from 'react';
import './data.css';
import { useHistory } from 'react-router-dom';
import { Box, TextField, Button, CloseIcon } from '@mui/material';
import axios from 'axios';
import { Select, MenuItem } from '@mui/material';




export default function Data() {
  
  const [config, setConfig] = useState({
    name: '',
    sets: 'number',
    reps: 'number',
    unit: 'number',
    date: 'number',
  });

  let history = useHistory();
  const submit = async (e) => {
    e.preventDefault();
    try {
      const data = await axios.post('http://localhost:3000/student', config);
    } catch (error) {
      console.log(error);
    }
    history.push('preview');
  };

  return (
    <>
      <div className='data'>
      
        <div className='mdata'>
          <h1>My Fitness Tracker</h1>
          <div>
            <Box
              component='form'
              sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
              }}
              noValidate
              autoComplete='off'
              

            >
              <Select value={2}>
                <MenuItem value={1}>Jan</MenuItem>
                <MenuItem value={2}>Feb</MenuItem>
                <MenuItem value={3}>March</MenuItem>
                <MenuItem value={4}>April</MenuItem>
                <MenuItem value={5}>May</MenuItem>
              
              <TextField
                required
                id='outlined-basic'
                
              
                onChange={(e) =>
                  setConfig((pv) => ({ ...pv, name: e.target.value }))
                }
                label='Exercise Name'
                variant='outlined'
              />
              </Select>
              
              <TextField
                required
                id='outlined-basic'
                onChange={(e) =>
                  setConfig((pv) => ({ ...pv, sets: e.target.value }))
                }
                label='Sets'
                variant='outlined'
              />
            </Box>
          </div>

          <div>
            <Box
              component='form'
              sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
              }}
              noValidate
              autoComplete='off'
            >
              <TextField
                required
                id='outlined-basic'
                onChange={(e) =>
                  setConfig((pv) => ({ ...pv, reps: e.target.value }))
                }
                label='Reps'
                variant='outlined'
              />
              <TextField
                required
                id='outlined-basic'
                onChange={(e) =>
                  setConfig((pv) => ({ ...pv, unit: e.target.value }))
                }
                label='Unit'
                variant='outlined'
              />
            </Box>
          </div>

          <div>
              
              
            <Box
              component='form'
              sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
                 
              }}
              noValidate
              autoComplete='off'
              
            >
              
              <TextField
               
                required
                id='outlined-basic'
                onChange={(e) =>
                  setConfig((pv) => ({ ...pv, date: e.target.value }))
                }
                
                label='Date'
                variant='outlined'
                type = 'Date'
              />
              
            </Box>
          </div>
          <div>
            <Button variant='contained' onClick={submit}>
              Submit
            </Button>
          </div>
        </div>
      </div>
    </>
  );
}
